inner join sierra_view.item_record_property as ItemRecProp on ItemRecProp.item_record_id = item.id
Join sierra_view.circ_trans as circ on circ.item_record_id = item.id

where item.location_code <> 'gmoff' and circ.transaction_gmt > now() - interval '28 day'
